import{_ as n,c as s,o as a,a as p}from"./app-D5GhxSZ1.js";const e="/yi-doc/assets/img_31-DPBjJjgM.png",t="/yi-doc/assets/img_32-DVG9EKiL.png",l={},i=p('<h1 id="八、树回归" tabindex="-1"><a class="header-anchor" href="#八、树回归"><span>八、树回归</span></a></h1><h2 id="_1、cart-树" tabindex="-1"><a class="header-anchor" href="#_1、cart-树"><span>1、Cart 树</span></a></h2><h3 id="_1-算法介绍" tabindex="-1"><a class="header-anchor" href="#_1-算法介绍"><span>(1) 算法介绍</span></a></h3><p>树回归是一种机器学习算法，它通过构建决策树来预测连续型变量的值。与分类树不同，树回归的目标是预测一个连续型的输出变量而不是离散型变量。树回归算法通过将特征空间分成若干个子区域，每个子区域拥有不同的输出值，从而构建一个决策树。</p><p>树回归算法通常采用递归二分法，将数据集切分成两个子集，在每个子集上递归地建立决策树，直到满足预设的停止条件。每次切分数据集时，树回归算法通过计算每个切分点的<strong>误差平方和</strong>来选择最佳的切分点。</p><p>在预测时，树回归算法从根节点开始遍历决策树，根据特征值和切分点的大小关系来选择左子树或右子树，直到到达叶子节点，叶子节点的预测值就是对应子区域的<strong>平均值</strong>。</p><p>树回归算法具有良好的可解释性和高效性，同时也能够处理缺失数据和离群点等问题。树回归在许多实际问题中都有很好的应用，比如金融预测、医学诊断和工业质量控制等。</p><h3 id="_2-算法步骤" tabindex="-1"><a class="header-anchor" href="#_2-算法步骤"><span>(2) 算法步骤</span></a></h3><p><img src="'+e+`" alt="img_31.png"></p><h3 id="_3-源码分析" tabindex="-1"><a class="header-anchor" href="#_3-源码分析"><span>(3) 源码分析</span></a></h3><p><strong>数据集概述</strong></p><p>只有一个特征，共 200 条数据。</p><div class="language-text line-numbers-mode" data-highlighter="prismjs" data-ext="text" data-title="text"><pre><code><span class="line">0.036098	0.155096</span>
<span class="line">0.993349	1.077553</span>
<span class="line">0.530897	0.893462</span>
<span class="line">0.712386	0.564858</span>
<span class="line">0.343554	-0.371700</span>
<span class="line">0.098016	-0.332760</span>
<span class="line">0.691115	0.834391</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>程序入口</strong></p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&quot;__main__&quot;</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token comment"># 回归树</span></span>
<span class="line">    myDat <span class="token operator">=</span> loadDataSet<span class="token punctuation">(</span><span class="token string">&#39;./data/data1.txt&#39;</span><span class="token punctuation">)</span></span>
<span class="line">    myMat <span class="token operator">=</span> mat<span class="token punctuation">(</span>myDat<span class="token punctuation">)</span></span>
<span class="line">    myTree <span class="token operator">=</span> createTree<span class="token punctuation">(</span>myMat<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">print</span><span class="token punctuation">(</span>myTree<span class="token punctuation">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>加载数据集</strong></p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 默认解析的数据是用tab分隔，并且是数值类型</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">loadDataSet</span><span class="token punctuation">(</span>fileName<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;loadDataSet(解析每一行，并转化为float类型)</span>
<span class="line">        Desc: 该函数读取一个以 tab 键为分隔符的文件，然后将每行的内容保存成一组浮点数</span>
<span class="line">    Args:</span>
<span class="line">        fileName 文件名</span>
<span class="line">    Returns:</span>
<span class="line">        dataMat 每一行的数据集array类型</span>
<span class="line">    Raises:</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    <span class="token comment"># 假定最后一列是结果值</span></span>
<span class="line">    <span class="token comment"># assume last column is target value</span></span>
<span class="line">    dataMat <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span></span>
<span class="line">    fr <span class="token operator">=</span> <span class="token builtin">open</span><span class="token punctuation">(</span>fileName<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">for</span> line <span class="token keyword">in</span> fr<span class="token punctuation">.</span>readlines<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        curLine <span class="token operator">=</span> line<span class="token punctuation">.</span>strip<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span>split<span class="token punctuation">(</span><span class="token string">&#39;\\t&#39;</span><span class="token punctuation">)</span></span>
<span class="line">        <span class="token comment"># 将所有的元素转化为float类型</span></span>
<span class="line">        <span class="token comment"># map all elements to float()</span></span>
<span class="line">        <span class="token comment"># map() 函数具体的含义，可见 https://my.oschina.net/zyzzy/blog/115096</span></span>
<span class="line">        fltLine <span class="token operator">=</span> <span class="token builtin">list</span><span class="token punctuation">(</span><span class="token builtin">map</span><span class="token punctuation">(</span><span class="token builtin">float</span><span class="token punctuation">,</span> curLine<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">        <span class="token comment"># fltLine = map(float, curLine)</span></span>
<span class="line">        dataMat<span class="token punctuation">.</span>append<span class="token punctuation">(</span>fltLine<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">return</span> dataMat</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>创建回归树</strong></p><p>选择一种最好的切分方式，切分完毕后，再分别以左右子树为根创建回归树。</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 假设 dataSet 是 NumPy Mat 类型的，那么我们可以进行 array 过滤</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">createTree</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> leafType<span class="token operator">=</span>regLeaf<span class="token punctuation">,</span> errType<span class="token operator">=</span>regErr<span class="token punctuation">,</span> ops<span class="token operator">=</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">4</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;createTree(获取回归树)</span>
<span class="line">        Description: 递归函数: 如果构建的是回归树，该模型是一个常数，如果是模型树，其模型师一个线性方程。</span>
<span class="line">    Args:</span>
<span class="line">        dataSet      加载的原始数据集</span>
<span class="line">        leafType     建立叶子点的函数</span>
<span class="line">        errType      误差计算函数</span>
<span class="line">        ops=(1, 4)   [容许误差下降值，切分的最少样本数]</span>
<span class="line">    Returns:</span>
<span class="line">        retTree    决策树最后的结果</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    <span class="token comment"># 选择最好的切分方式:  feature索引值，最优切分值</span></span>
<span class="line">    <span class="token comment"># choose the best split</span></span>
<span class="line">    feat<span class="token punctuation">,</span> val <span class="token operator">=</span> chooseBestSplit<span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> leafType<span class="token punctuation">,</span> errType<span class="token punctuation">,</span> ops<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># if the splitting hit a stop condition return val</span></span>
<span class="line">    <span class="token comment"># 如果 splitting 达到一个停止条件，那么返回 val</span></span>
<span class="line">    <span class="token triple-quoted-string string">&#39;&#39;&#39;</span>
<span class="line">    *** 最后的返回结果是最后剩下的 val，也就是len小于topN的集合</span>
<span class="line">    &#39;&#39;&#39;</span></span>
<span class="line">    <span class="token keyword">if</span> feat <span class="token keyword">is</span> <span class="token boolean">None</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">return</span> val</span>
<span class="line">    retTree <span class="token operator">=</span> <span class="token punctuation">{</span><span class="token punctuation">}</span></span>
<span class="line">    retTree<span class="token punctuation">[</span><span class="token string">&#39;spInd&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> feat</span>
<span class="line">    retTree<span class="token punctuation">[</span><span class="token string">&#39;spVal&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> val</span>
<span class="line">    <span class="token comment"># 大于在右边，小于在左边，分为2个数据集</span></span>
<span class="line">    lSet<span class="token punctuation">,</span> rSet <span class="token operator">=</span> binSplitDataSet<span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> feat<span class="token punctuation">,</span> val<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 递归的进行调用，在左右子树中继续递归生成树</span></span>
<span class="line">    retTree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> createTree<span class="token punctuation">(</span>lSet<span class="token punctuation">,</span> leafType<span class="token punctuation">,</span> errType<span class="token punctuation">,</span> ops<span class="token punctuation">)</span></span>
<span class="line">    retTree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> createTree<span class="token punctuation">(</span>rSet<span class="token punctuation">,</span> leafType<span class="token punctuation">,</span> errType<span class="token punctuation">,</span> ops<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">return</span> retTree</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>选择最优的切分方式</strong></p><p>参数中的<code>ops=(1, 4)</code>是预剪枝的参数，规定了停止划分子树的条件，分别表示<strong>容许误差下降值</strong>和<strong>切分的最少样本数</strong>。叶子结点样本数小于<strong>切分的最少样本数</strong>或误差平方和小于数据集误差平方和-<strong>容许误差下降值</strong>，都无需再进行划分。</p><p>以每个特征的所有值为基准划分数据集D，两个子数据集D1,D2误差平方和最小时变为最佳切分方式。</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 1.用最佳方式切分数据集</span></span>
<span class="line"><span class="token comment"># 2.生成相应的叶节点</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">chooseBestSplit</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> leafType<span class="token operator">=</span>regLeaf<span class="token punctuation">,</span> errType<span class="token operator">=</span>regErr<span class="token punctuation">,</span> ops<span class="token operator">=</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">4</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;chooseBestSplit(用最佳方式切分数据集 和 生成相应的叶节点)</span>
<span class="line"></span>
<span class="line">    Args:</span>
<span class="line">        dataSet   加载的原始数据集</span>
<span class="line">        leafType  建立叶子点的函数</span>
<span class="line">        errType   误差计算函数(求总方差)</span>
<span class="line">        ops       [容许误差下降值，切分的最少样本数]。</span>
<span class="line">    Returns:</span>
<span class="line">        bestIndex feature的index坐标</span>
<span class="line">        bestValue 切分的最优值</span>
<span class="line">    Raises:</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># ops=(1,4)，非常重要，因为它决定了决策树划分停止的threshold值，被称为预剪枝（prepruning），其实也就是用于控制函数的停止时机。</span></span>
<span class="line">    <span class="token comment"># 之所以这样说，是因为它防止决策树的过拟合，所以当误差的下降值小于tolS，或划分后的集合size小于tolN时，选择停止继续划分。</span></span>
<span class="line">    <span class="token comment"># 最小误差下降值，划分后的误差减小小于这个差值，就不用继续划分</span></span>
<span class="line">    tolS <span class="token operator">=</span> ops<span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span></span>
<span class="line">    <span class="token comment"># 划分最小 size 小于，就不继续划分了</span></span>
<span class="line">    tolN <span class="token operator">=</span> ops<span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span></span>
<span class="line">    <span class="token comment"># 如果结果集(最后一列为1个变量)，就返回退出</span></span>
<span class="line">    <span class="token comment"># .T 对数据集进行转置</span></span>
<span class="line">    <span class="token comment"># .tolist()[0] 转化为数组并取第0列</span></span>
<span class="line">    <span class="token keyword">if</span> <span class="token builtin">len</span><span class="token punctuation">(</span><span class="token builtin">set</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">.</span>T<span class="token punctuation">.</span>tolist<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token operator">==</span> <span class="token number">1</span><span class="token punctuation">:</span> <span class="token comment"># 如果集合size为1，也就是说全部的数据都是同一个类别，不用继续划分。</span></span>
<span class="line">        <span class="token comment">#  exit cond 1</span></span>
<span class="line">        <span class="token keyword">return</span> <span class="token boolean">None</span><span class="token punctuation">,</span> leafType<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 计算行列值</span></span>
<span class="line">    m<span class="token punctuation">,</span> n <span class="token operator">=</span> shape<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 无分类误差的总方差和</span></span>
<span class="line">    <span class="token comment"># the choice of the best feature is driven by Reduction in RSS error from mean</span></span>
<span class="line">    S <span class="token operator">=</span> errType<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># inf 正无穷大</span></span>
<span class="line">    bestS<span class="token punctuation">,</span> bestIndex<span class="token punctuation">,</span> bestValue <span class="token operator">=</span> inf<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span></span>
<span class="line">    <span class="token comment"># 循环处理每一列对应的feature值</span></span>
<span class="line">    <span class="token keyword">for</span> featIndex <span class="token keyword">in</span> <span class="token builtin">range</span><span class="token punctuation">(</span>n<span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">:</span> <span class="token comment"># 对于每个特征</span></span>
<span class="line">        <span class="token comment"># [0]表示这一列的[所有行]，不要[0]就是一个array[[所有行]]，下面的一行表示的是将某一列全部的数据转换为行，然后设置为list形式</span></span>
<span class="line">        <span class="token keyword">for</span> splitVal <span class="token keyword">in</span> <span class="token builtin">set</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> featIndex<span class="token punctuation">]</span><span class="token punctuation">.</span>T<span class="token punctuation">.</span>tolist<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">            <span class="token comment"># 对该列进行分组，然后组内的成员的val值进行 二元切分</span></span>
<span class="line">            mat0<span class="token punctuation">,</span> mat1 <span class="token operator">=</span> binSplitDataSet<span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> featIndex<span class="token punctuation">,</span> splitVal<span class="token punctuation">)</span></span>
<span class="line">            <span class="token comment"># 判断二元切分的方式的元素数量是否符合预期</span></span>
<span class="line">            <span class="token keyword">if</span> <span class="token punctuation">(</span>shape<span class="token punctuation">(</span>mat0<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span> <span class="token operator">&lt;</span> tolN<span class="token punctuation">)</span> <span class="token keyword">or</span> <span class="token punctuation">(</span>shape<span class="token punctuation">(</span>mat1<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span> <span class="token operator">&lt;</span> tolN<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">                <span class="token keyword">continue</span></span>
<span class="line">            newS <span class="token operator">=</span> errType<span class="token punctuation">(</span>mat0<span class="token punctuation">)</span> <span class="token operator">+</span> errType<span class="token punctuation">(</span>mat1<span class="token punctuation">)</span></span>
<span class="line">            <span class="token comment"># 如果二元切分，算出来的误差在可接受范围内，那么就记录切分点，并记录最小误差</span></span>
<span class="line">            <span class="token comment"># 如果划分后误差小于 bestS，则说明找到了新的bestS</span></span>
<span class="line">            <span class="token keyword">if</span> newS <span class="token operator">&lt;</span> bestS<span class="token punctuation">:</span></span>
<span class="line">                bestIndex <span class="token operator">=</span> featIndex</span>
<span class="line">                bestValue <span class="token operator">=</span> splitVal</span>
<span class="line">                bestS <span class="token operator">=</span> newS</span>
<span class="line">    <span class="token comment"># 判断二元切分的方式的元素误差是否符合预期</span></span>
<span class="line">    <span class="token comment"># if the decrease (S-bestS) is less than a threshold don&#39;t do the split</span></span>
<span class="line">    <span class="token keyword">if</span> <span class="token punctuation">(</span>S <span class="token operator">-</span> bestS<span class="token punctuation">)</span> <span class="token operator">&lt;</span> tolS<span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">return</span> <span class="token boolean">None</span><span class="token punctuation">,</span> leafType<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    mat0<span class="token punctuation">,</span> mat1 <span class="token operator">=</span> binSplitDataSet<span class="token punctuation">(</span>dataSet<span class="token punctuation">,</span> bestIndex<span class="token punctuation">,</span> bestValue<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 对整体的成员进行判断，是否符合预期</span></span>
<span class="line">    <span class="token comment"># 如果集合的 size 小于 tolN</span></span>
<span class="line">    <span class="token keyword">if</span> <span class="token punctuation">(</span>shape<span class="token punctuation">(</span>mat0<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span> <span class="token operator">&lt;</span> tolN<span class="token punctuation">)</span> <span class="token keyword">or</span> <span class="token punctuation">(</span>shape<span class="token punctuation">(</span>mat1<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span> <span class="token operator">&lt;</span> tolN<span class="token punctuation">)</span><span class="token punctuation">:</span> <span class="token comment"># 当最佳划分后，集合过小，也不划分，产生叶节点</span></span>
<span class="line">        <span class="token keyword">return</span> <span class="token boolean">None</span><span class="token punctuation">,</span> leafType<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">return</span> bestIndex<span class="token punctuation">,</span> bestValue</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>数据集误差平方和计算</strong></p><p>方差=误差平方和/样本数</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token keyword">def</span> <span class="token function">regErr</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token comment"># shape(dataSet)[0] 表示行数</span></span>
<span class="line">    <span class="token keyword">return</span> var<span class="token punctuation">(</span>dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token operator">*</span> shape<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>返回叶子结点的值</strong></p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 返回每一个叶子结点的均值</span></span>
<span class="line"><span class="token comment"># returns the value used for each leaf</span></span>
<span class="line"><span class="token comment"># 我的理解是: regLeaf 是产生叶节点的函数，就是求均值，即用聚类中心点来代表这类数据</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">regLeaf</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token keyword">return</span> mean<span class="token punctuation">(</span>dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="_4-sklearn-cart-树示例代码" tabindex="-1"><a class="header-anchor" href="#_4-sklearn-cart-树示例代码"><span>(4) sklearn cart 树示例代码</span></a></h3><p>在 scikit-learn 中使用 CART 回归树模型，可以调整的参数包括：</p><ol><li><strong>criterion</strong>：衡量分裂质量的评价标准，可选的值有&quot;gini&quot;和&quot;entropy&quot;，默认值是&quot;gini&quot;。</li><li><strong>splitter</strong>：决定分裂节点的策略，可选的值有&quot;best&quot;和&quot;random&quot;，默认值是&quot;best&quot;。</li><li><strong>max_depth</strong>：决策树的最大深度，如果不设置则不限制深度。</li><li><strong>min_samples_split</strong>：分裂一个内部节点需要的最小样本数，如果某个节点的样本数少于这个值，则不会再分裂。</li><li><strong>min_samples_leaf</strong>：一个叶子节点所需的最小样本数，如果某个叶子节点的样本数少于这个值，则会和兄弟节点一起被剪枝。</li><li><strong>min_weight_fraction_leaf</strong>：一个叶子节点所需的最小加权样本数的总和的比例。</li><li><strong>max_features</strong>：在分裂节点时考虑的特征数量，可选的值有&quot;auto&quot;、&quot;sqrt&quot;、&quot;log2&quot;和None，可以设置为整数来表示考虑的特征数量。</li><li><strong>random_state</strong>：控制随机性的种子值，用于重现结果。</li></ol><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token keyword">from</span> sklearn<span class="token punctuation">.</span>datasets <span class="token keyword">import</span> load_diabetes</span>
<span class="line"><span class="token keyword">from</span> sklearn<span class="token punctuation">.</span>tree <span class="token keyword">import</span> DecisionTreeRegressor</span>
<span class="line"><span class="token keyword">from</span> sklearn<span class="token punctuation">.</span>model_selection <span class="token keyword">import</span> train_test_split</span>
<span class="line"><span class="token keyword">from</span> sklearn<span class="token punctuation">.</span>metrics <span class="token keyword">import</span> mean_squared_error</span>
<span class="line"></span>
<span class="line"><span class="token comment"># 加载糖尿病数据集</span></span>
<span class="line">diabetes <span class="token operator">=</span> load_diabetes<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
<span class="line">X<span class="token punctuation">,</span> y <span class="token operator">=</span> diabetes<span class="token punctuation">.</span>data<span class="token punctuation">,</span> diabetes<span class="token punctuation">.</span>target</span>
<span class="line"></span>
<span class="line"><span class="token comment"># 划分训练集和测试集</span></span>
<span class="line">X_train<span class="token punctuation">,</span> X_test<span class="token punctuation">,</span> y_train<span class="token punctuation">,</span> y_test <span class="token operator">=</span> train_test_split<span class="token punctuation">(</span>X<span class="token punctuation">,</span> y<span class="token punctuation">,</span> test_size<span class="token operator">=</span><span class="token number">0.2</span><span class="token punctuation">,</span> random_state<span class="token operator">=</span><span class="token number">42</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"><span class="token comment"># 训练CART回归树模型</span></span>
<span class="line">model <span class="token operator">=</span> DecisionTreeRegressor<span class="token punctuation">(</span>random_state<span class="token operator">=</span><span class="token number">42</span><span class="token punctuation">)</span></span>
<span class="line">model<span class="token punctuation">.</span>fit<span class="token punctuation">(</span>X_train<span class="token punctuation">,</span> y_train<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"><span class="token comment"># 在测试集上进行预测并评估模型性能</span></span>
<span class="line">y_pred <span class="token operator">=</span> model<span class="token punctuation">.</span>predict<span class="token punctuation">(</span>X_test<span class="token punctuation">)</span></span>
<span class="line">mse <span class="token operator">=</span> mean_squared_error<span class="token punctuation">(</span>y_test<span class="token punctuation">,</span> y_pred<span class="token punctuation">)</span></span>
<span class="line"><span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&quot;Mean Squared Error: {:.2f}&quot;</span><span class="token punctuation">.</span><span class="token builtin">format</span><span class="token punctuation">(</span>mse<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="_2、树剪枝" tabindex="-1"><a class="header-anchor" href="#_2、树剪枝"><span>2、树剪枝</span></a></h2><h3 id="_1-基本介绍" tabindex="-1"><a class="header-anchor" href="#_1-基本介绍"><span>(1) 基本介绍</span></a></h3><p>树剪枝是一种用于决策树模型的优化技术，旨在减少模型的复杂度并提高模型的泛化能力。在决策树模型中，剪枝是通过去除树的一部分（即分支或叶子节点）来实现的，以达到减少模型复杂度的目的。</p><p>决策树模型在拟合训练数据时容易出现过拟合的情况，这时候模型可能过于复杂，过多地捕捉到噪声和随机性，并且不能很好地推广到新的数据集上。树剪枝的目的是缓解过拟合，以提高模型的泛化能力。</p><p>树剪枝可以分为预剪枝和后剪枝两种类型：</p><ol><li>预剪枝：在决策树的生长过程中，在某些条件满足时提前停止树的分裂，从而避免过拟合。这些条件可能包括节点的深度、最小样本数、最小信息增益等。上述源码中根据<strong>容许误差下降值</strong>和<strong>切分的最少样本数</strong>停止分裂就是预剪枝。</li><li>后剪枝：在决策树生成完毕之后，通过从树的叶子节点开始删除一些节点（即子树），并将其父节点转换为叶子节点来缩小树的规模，从而提高模型泛化能力。具体来说，后剪枝是在验证集上评估子树的泛化性能，选择最佳子树并将其剪枝。</li></ol><p>树剪枝是一种很有用的技术，可以在一定程度上避免决策树模型的过拟合问题，提高模型的泛化能力。但是，树剪枝需要调整一些参数，比如最小样本数、最小信息增益等，因此需要对数据集和模型进行细致的调试和优化。</p><h3 id="_2-后剪枝" tabindex="-1"><a class="header-anchor" href="#_2-后剪枝"><span>(2) 后剪枝</span></a></h3><p>后剪枝技术是树剪枝的一种类型，它是一种减少决策树复杂度的优化方法，能够提高决策树的泛化性能。与预剪枝不同，后剪枝是在决策树生成完毕之后进行的。</p><p>后剪枝的基本思想是先构建一颗完整的决策树，然后利用验证集数据对树进行剪枝。剪枝的过程是从叶子节点开始，不断地将某个子树替换为一个叶子节点，直到剪枝后的树在验证集上的泛化误差不再提高。在剪枝过程中，通过对比剪枝前后的验证集误差来确定哪些子树应该被剪掉，剩下的子树形成最终的简化决策树。</p><p>后剪枝过程的具体步骤如下：</p><ol><li>从叶子节点开始向上回缩，对每个节点进行以下操作：</li></ol><ul><li>将该节点及其所有子节点作为一颗子树，计算这个子树在验证集上的误差。</li><li>计算将该节点替换为叶子节点后在验证集上的误差。</li><li>如果替换后的误差比原来的误差更小，则将该节点替换为叶子节点。</li></ul><ol start="2"><li>不断重复步骤1，直到无法再进行剪枝为止，形成最终的简化决策树。</li></ol><p>后剪枝技术可以有效地避免过拟合问题，提高决策树模型的泛化性能。它的优点在于生成的树可以完全利用训练集数据，并且不需要提前设置参数，但是需要独立的验证集数据用于剪枝。需要注意的是，在决策树剪枝时，应该避免将过多的子树剪掉，否则可能会导致欠拟合问题。</p><h3 id="_3-源码分析-1" tabindex="-1"><a class="header-anchor" href="#_3-源码分析-1"><span>(3) 源码分析</span></a></h3><p><strong>程序入口</strong></p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&quot;__main__&quot;</span><span class="token punctuation">:</span></span>
<span class="line">	<span class="token comment"># 回归树</span></span>
<span class="line">    myDat <span class="token operator">=</span> loadDataSet<span class="token punctuation">(</span><span class="token string">&#39;./data/data1.txt&#39;</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># myDat = loadDataSet(&#39;data/9.RegTrees/data2.txt&#39;)</span></span>
<span class="line">    <span class="token comment"># print &#39;myDat=&#39;, myDat</span></span>
<span class="line">    myMat <span class="token operator">=</span> mat<span class="token punctuation">(</span>myDat<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># print &#39;myMat=&#39;,  myMat</span></span>
<span class="line">    myTree <span class="token operator">=</span> createTree<span class="token punctuation">(</span>myMat<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">print</span><span class="token punctuation">(</span>myTree<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># # 2. 后剪枝就是: 通过测试数据，对预测模型进行合并判断</span></span>
<span class="line">    myDatTest <span class="token operator">=</span> loadDataSet<span class="token punctuation">(</span><span class="token string">&#39;./data/data3test.txt&#39;</span><span class="token punctuation">)</span></span>
<span class="line">    myMat2Test <span class="token operator">=</span> mat<span class="token punctuation">(</span>myDatTest<span class="token punctuation">)</span></span>
<span class="line">    myFinalTree <span class="token operator">=</span> prune<span class="token punctuation">(</span>myTree<span class="token punctuation">,</span> myMat2Test<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&#39;\\n\\n\\n-------------------&#39;</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">print</span><span class="token punctuation">(</span>myFinalTree<span class="token punctuation">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>剪枝</strong></p><p>按照回归树划分测试数据集，若两个叶子结点误差平方和 &gt; 合并为一个叶子结点的误差平方和，就进行合并(剪枝)。</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 检查是否适合合并分枝</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">prune</span><span class="token punctuation">(</span>tree<span class="token punctuation">,</span> testData<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;</span>
<span class="line">    Desc:</span>
<span class="line">        从上而下找到叶节点，用测试数据集来判断将这些叶节点合并是否能降低测试误差</span>
<span class="line">    Args:</span>
<span class="line">        tree -- 待剪枝的树</span>
<span class="line">        testData -- 剪枝所需要的测试数据 testData</span>
<span class="line">    Returns:</span>
<span class="line">        tree -- 剪枝完成的树</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    <span class="token comment"># 判断是否测试数据集没有数据，如果没有，就直接返回tree本身的均值</span></span>
<span class="line">    <span class="token keyword">if</span> shape<span class="token punctuation">(</span>testData<span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span> <span class="token operator">==</span> <span class="token number">0</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">return</span> getMean<span class="token punctuation">(</span>tree<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># 判断分枝是否是dict字典，如果是就将测试数据集进行切分</span></span>
<span class="line">    <span class="token keyword">if</span> <span class="token punctuation">(</span>isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token keyword">or</span> isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        lSet<span class="token punctuation">,</span> rSet <span class="token operator">=</span> binSplitDataSet<span class="token punctuation">(</span>testData<span class="token punctuation">,</span> tree<span class="token punctuation">[</span><span class="token string">&#39;spInd&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> tree<span class="token punctuation">[</span><span class="token string">&#39;spVal&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 如果是左边分枝是字典，就传入左边的数据集和左边的分枝，进行递归</span></span>
<span class="line">    <span class="token keyword">if</span> isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> prune<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> lSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 如果是右边分枝是字典，就传入左边的数据集和左边的分枝，进行递归</span></span>
<span class="line">    <span class="token keyword">if</span> isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span> <span class="token operator">=</span> prune<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> rSet<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># 上面的一系列操作本质上就是将测试数据集按照训练完成的树拆分好，对应的值放到对应的节点</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># 如果左右两边同时都不是dict字典，也就是左右两边都是叶节点，而不是子树了，那么分割测试数据集。</span></span>
<span class="line">    <span class="token comment"># 1. 如果正确</span></span>
<span class="line">    <span class="token comment">#   * 那么计算一下总方差 和 该结果集的本身不分枝的总方差比较</span></span>
<span class="line">    <span class="token comment">#   * 如果 合并的总方差 &lt; 不合并的总方差，那么就进行合并</span></span>
<span class="line">    <span class="token comment"># 注意返回的结果:  如果可以合并，原来的dict就变为了 数值</span></span>
<span class="line">    <span class="token keyword">if</span> <span class="token keyword">not</span> isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token keyword">and</span> <span class="token keyword">not</span> isTree<span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        lSet<span class="token punctuation">,</span> rSet <span class="token operator">=</span> binSplitDataSet<span class="token punctuation">(</span>testData<span class="token punctuation">,</span> tree<span class="token punctuation">[</span><span class="token string">&#39;spInd&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> tree<span class="token punctuation">[</span><span class="token string">&#39;spVal&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span></span>
<span class="line">        <span class="token comment"># power(x, y)表示x的y次方；这时tree[&#39;left&#39;]和tree[&#39;right&#39;]都是具体数值</span></span>
<span class="line">        errorNoMerge <span class="token operator">=</span> <span class="token builtin">sum</span><span class="token punctuation">(</span>power<span class="token punctuation">(</span>lSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">-</span> tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token operator">+</span> <span class="token builtin">sum</span><span class="token punctuation">(</span>power<span class="token punctuation">(</span>rSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">-</span> tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">        treeMean <span class="token operator">=</span> <span class="token punctuation">(</span>tree<span class="token punctuation">[</span><span class="token string">&#39;left&#39;</span><span class="token punctuation">]</span> <span class="token operator">+</span> tree<span class="token punctuation">[</span><span class="token string">&#39;right&#39;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token operator">/</span><span class="token number">2.0</span></span>
<span class="line">        errorMerge <span class="token operator">=</span> <span class="token builtin">sum</span><span class="token punctuation">(</span>power<span class="token punctuation">(</span>testData<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">-</span> treeMean<span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">        <span class="token comment"># 如果 合并的总方差 &lt; 不合并的总方差，那么就进行合并</span></span>
<span class="line">        <span class="token keyword">if</span> errorMerge <span class="token operator">&lt;</span> errorNoMerge<span class="token punctuation">:</span></span>
<span class="line">            <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&quot;merging&quot;</span><span class="token punctuation">)</span></span>
<span class="line">            <span class="token keyword">return</span> treeMean</span>
<span class="line">        <span class="token comment"># 两个return可以简化成一个</span></span>
<span class="line">        <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">            <span class="token keyword">return</span> tree</span>
<span class="line">    <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">return</span> tree</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="_3、模型树" tabindex="-1"><a class="header-anchor" href="#_3、模型树"><span>3、模型树</span></a></h2><h3 id="_1-基本介绍-1" tabindex="-1"><a class="header-anchor" href="#_1-基本介绍-1"><span>(1) 基本介绍</span></a></h3><p>模型树是一种集成学习方法，它结合了决策树和线性回归模型。在模型树中，每个叶节点都对应一个线性回归模型，而每个非叶节点都对应一个特征和阈值，用于将样本划分到子树中。</p><p>模型树可以看作是一种特殊的回归树，其中每个叶节点都包含一个线性回归模型。与普通的回归树相比，模型树可以更好地拟合非线性关系，并且具有更好的可解释性。由于模型树可以自动选择特征并且使用线性回归模型进行预测，因此它可以处理具有大量特征的数据集，并且可以在不需要特征选择的情况下进行预测。</p><p>在构建模型树时，通常使用递归二分法对数据集进行划分，并在每个叶节点上训练一个线性回归模型。预测时，输入数据被传递到根节点，然后根据特征和阈值将数据划分到子树中，并在叶节点上应用相应的线性回归模型进行预测。</p><p>总之，模型树是一种强大的集成学习方法，它可以更好地拟合非线性关系，并且具有更好的可解释性和灵活性。</p><h3 id="_2-源码分析" tabindex="-1"><a class="header-anchor" href="#_2-源码分析"><span>(2) 源码分析</span></a></h3><p>模型树代码和上述 Cart 树代码几乎完全相同，不同点在于<strong>误差的计算方式</strong>和<strong>叶节点值预测方式</strong>。</p><p><strong>误差计算</strong></p><p>对数据集进行线性拟合，误差值是预测值与实际值差值的平方和。</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token comment"># 计算线性模型的误差值</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">modelErr</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;</span>
<span class="line">    Desc:</span>
<span class="line">        在给定数据集上计算误差。</span>
<span class="line">    Args:</span>
<span class="line">        dataSet -- 输入数据集</span>
<span class="line">    Returns:</span>
<span class="line">        调用 linearSolve 函数，返回 yHat 和 Y 之间的平方误差。</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    ws<span class="token punctuation">,</span> X<span class="token punctuation">,</span> Y <span class="token operator">=</span> linearSolve<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    yHat <span class="token operator">=</span> X <span class="token operator">*</span> ws</span>
<span class="line">    <span class="token comment"># print corrcoef(yHat, Y, rowvar=0)</span></span>
<span class="line">    <span class="token keyword">return</span> <span class="token builtin">sum</span><span class="token punctuation">(</span>power<span class="token punctuation">(</span>Y <span class="token operator">-</span> yHat<span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"> <span class="token comment"># helper function used in two places</span></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">linearSolve</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;</span>
<span class="line">    Desc:</span>
<span class="line">        将数据集格式化成目标变量Y和自变量X，执行简单的线性回归，得到ws</span>
<span class="line">    Args:</span>
<span class="line">        dataSet -- 输入数据</span>
<span class="line">    Returns:</span>
<span class="line">        ws -- 执行线性回归的回归系数</span>
<span class="line">        X -- 格式化自变量X</span>
<span class="line">        Y -- 格式化目标变量Y</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    m<span class="token punctuation">,</span> n <span class="token operator">=</span> shape<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 产生一个关于1的矩阵</span></span>
<span class="line">    X <span class="token operator">=</span> mat<span class="token punctuation">(</span>ones<span class="token punctuation">(</span><span class="token punctuation">(</span>m<span class="token punctuation">,</span> n<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">    Y <span class="token operator">=</span> mat<span class="token punctuation">(</span>ones<span class="token punctuation">(</span><span class="token punctuation">(</span>m<span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># X的0列为1，常数项，用于计算平衡误差</span></span>
<span class="line">    X<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">:</span> n<span class="token punctuation">]</span> <span class="token operator">=</span> dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">:</span> n<span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span></span>
<span class="line">    Y <span class="token operator">=</span> dataSet<span class="token punctuation">[</span><span class="token punctuation">:</span><span class="token punctuation">,</span> <span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">]</span></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># 转置矩阵*矩阵</span></span>
<span class="line">    xTx <span class="token operator">=</span> X<span class="token punctuation">.</span>T <span class="token operator">*</span> X</span>
<span class="line">    <span class="token comment"># 如果矩阵的逆不存在，会造成程序异常</span></span>
<span class="line">    <span class="token keyword">if</span> linalg<span class="token punctuation">.</span>det<span class="token punctuation">(</span>xTx<span class="token punctuation">)</span> <span class="token operator">==</span> <span class="token number">0.0</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">raise</span> NameError<span class="token punctuation">(</span><span class="token string">&#39;This matrix is singular, cannot do inverse,\\ntry increasing the second value of ops&#39;</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 最小二乘法求最优解:  w0*1+w1*x1=y</span></span>
<span class="line">    ws <span class="token operator">=</span> xTx<span class="token punctuation">.</span>I <span class="token operator">*</span> <span class="token punctuation">(</span>X<span class="token punctuation">.</span>T <span class="token operator">*</span> Y<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">return</span> ws<span class="token punctuation">,</span> X<span class="token punctuation">,</span> Y</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>叶节点值预测方式</strong></p><p>拟合叶节点数据称为线性模型进行预测。</p><div class="language-python line-numbers-mode" data-highlighter="prismjs" data-ext="py" data-title="py"><pre><code><span class="line"><span class="token keyword">def</span> <span class="token function">modelLeaf</span><span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token triple-quoted-string string">&quot;&quot;&quot;</span>
<span class="line">    Desc:</span>
<span class="line">        当数据不再需要切分的时候，生成叶节点的模型。</span>
<span class="line">    Args:</span>
<span class="line">        dataSet -- 输入数据集</span>
<span class="line">    Returns:</span>
<span class="line">        调用 linearSolve 函数，返回得到的 回归系数ws</span>
<span class="line">    &quot;&quot;&quot;</span></span>
<span class="line">    ws<span class="token punctuation">,</span> X<span class="token punctuation">,</span> Y <span class="token operator">=</span> linearSolve<span class="token punctuation">(</span>dataSet<span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">return</span> ws</span>
<span class="line"></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><img src="`+t+'" alt="img_32.png"></p>',70),c=[i];function o(u,r){return a(),s("div",null,c)}const k=n(l,[["render",o],["__file","8-树回归.html.vue"]]),m=JSON.parse('{"path":"/ml/traditional_ml/8-%E6%A0%91%E5%9B%9E%E5%BD%92.html","title":"八、树回归","lang":"zh-CN","frontmatter":{},"headers":[{"level":2,"title":"1、Cart 树","slug":"_1、cart-树","link":"#_1、cart-树","children":[{"level":3,"title":"(1) 算法介绍","slug":"_1-算法介绍","link":"#_1-算法介绍","children":[]},{"level":3,"title":"(2) 算法步骤","slug":"_2-算法步骤","link":"#_2-算法步骤","children":[]},{"level":3,"title":"(3) 源码分析","slug":"_3-源码分析","link":"#_3-源码分析","children":[]},{"level":3,"title":"(4) sklearn cart 树示例代码","slug":"_4-sklearn-cart-树示例代码","link":"#_4-sklearn-cart-树示例代码","children":[]}]},{"level":2,"title":"2、树剪枝","slug":"_2、树剪枝","link":"#_2、树剪枝","children":[{"level":3,"title":"(1) 基本介绍","slug":"_1-基本介绍","link":"#_1-基本介绍","children":[]},{"level":3,"title":"(2) 后剪枝","slug":"_2-后剪枝","link":"#_2-后剪枝","children":[]},{"level":3,"title":"(3) 源码分析","slug":"_3-源码分析-1","link":"#_3-源码分析-1","children":[]}]},{"level":2,"title":"3、模型树","slug":"_3、模型树","link":"#_3、模型树","children":[{"level":3,"title":"(1) 基本介绍","slug":"_1-基本介绍-1","link":"#_1-基本介绍-1","children":[]},{"level":3,"title":"(2) 源码分析","slug":"_2-源码分析","link":"#_2-源码分析","children":[]}]}],"git":{"updatedTime":1721739933000,"contributors":[{"name":"ZhangZiYi","email":"ecustzzy1@163.com","commits":1}]},"filePathRelative":"ml/traditional_ml/8-树回归.md"}');export{k as comp,m as data};
