<span class="line">        <span class="token class-name">String</span> jedistest <span class="token operator">=</span> jedis<span class="token punctuation">.</span><span class="token function">get</span><span class="token punctuation">(</span><span class="token string">"jedistest"</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
